$(document).on("change", ".countrypicker", function(){
    //button = $(".country-selector").children('.btn');
    button = $(this).parent().children('.btn');

    def = $(this).find(':selected').data('country-code');
    flagIcon = flags_url + '/' + def + '.png';
    button.css("background-size", '20px');
    button.css("background-position", '10px 12px');
    button.css("background-repeat", 'no-repeat');
    button.css("background-image", "url('" + flagIcon + "'");
    button.css("border-radius", '4px');
    button.css("border", '1px solid #ced4da');
    button.css("background-color", 'transparent');
    button.css("padding", '11px 30px 11px 40px');
});